mapper.select:
SELECT ${key} "key", ${value} "value" FROM ${table} 

	WHERE ${where}

ORDER BY ${order}

SELECT 
	u.*, c.username createuser, m.username modifyuser,
	cab.nama cabang
FROM lst_user u
LEFT OUTER JOIN lst_user c ON u.createby = c.id
LEFT OUTER JOIN lst_user m ON u.modifyby = m.id
LEFT OUTER JOIN lst_cabang cab ON u.cabang_id = cab.id

	WHERE u.username = #{username}
	WHERE u.id = #{id}


	AND u.password = #{password}

SELECT * FROM lst_user
WHERE UPPER(username) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%')
ORDER BY cabang_id, username LIMIT #{offset}, #{rowcount}

SELECT gu.* 
FROM lst_group_user gu
WHERE gu.id = #{id}

SELECT * FROM lst_group_user
WHERE UPPER(nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%')
ORDER BY nama LIMIT #{offset}, #{rowcount}

SELECT * FROM lst_supir s
WHERE (s.id LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR s.nama LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
ORDER BY s.nama LIMIT #{offset}, #{rowcount}

SELECT * 
FROM mst_customer
WHERE id = #{id}

SELECT * FROM lst_barang b
WHERE (b.id LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(b.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
ORDER BY b.nama LIMIT #{offset}, #{rowcount}

SELECT s.*, k.keterangan jenisName FROM lst_mobil s LEFT OUTER JOIN lst_config k ON s.jenis = k.jenis AND k.id = 4
WHERE (UPPER(s.no_polisi) LIKE CONCAT('%', UPPER(#{search}), '%') )
ORDER BY s.no_polisi LIMIT #{offset}, #{rowcount}

SELECT s.*, k.keterangan jenisName FROM lst_kapal s LEFT OUTER JOIN lst_config k ON s.jenis = k.jenis AND k.id = 5
WHERE (UPPER(s.kode) LIKE CONCAT('%', UPPER(#{search}), '%') OR UPPER(s.nama) LIKE CONCAT('%', UPPER(#{search}), '%') )
ORDER BY s.kode, s.nama LIMIT #{offset}, #{rowcount}

SELECT s.* , x.nama customerName, y.nama barangName FROM lst_harga s LEFT OUTER JOIN mst_customer x ON s.customer_id = x.id LEFT OUTER JOIN lst_barang y ON s.barang_id = y.id
WHERE (UPPER(x.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(y.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
ORDER BY x.nama, y.nama LIMIT #{offset}, #{rowcount}

SELECT * FROM lst_cabang b
WHERE (b.id LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(b.kode) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(b.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
ORDER BY b.kode, b.nama LIMIT #{offset}, #{rowcount}

SELECT t.*, c.nama customer, b.nama cabang, c2.nama tujuan
FROM mst_trans t, mst_customer c, lst_cabang b, mst_customer c2
WHERE t.customer_id = c.id AND t.cabang_id = b.id AND t.tujuan_id = c2.id

	AND (
		UPPER(t.no_stt) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR
		UPPER(c.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%')
	)


	AND t.tgl_stt >= #{periodFrom}


	AND t.tgl_stt <= #{periodTo}		

ORDER BY t.no_stt DESC
LIMIT #{offset}, #{rowcount}

SELECT count(*)
FROM mst_trans t, mst_customer c, lst_cabang b
WHERE t.customer_id = c.id AND t.cabang_id = b.id

	AND (
		UPPER(t.no_stt) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR
		UPPER(c.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%')
	)


	AND t.tgl_stt >= #{periodFrom}


	AND t.tgl_stt <= #{periodTo}

select child.path,child.parent, parent.nama parent_nama, parent.link parent_link, child.id, child.nama, child.link, child.level, child.urut from lst_menu child INNER JOIN lst_menu parent on child.parent = parent.id and parent.active=true where child.active=true
and child.id = #{value}
order by child.path, child.urut

select * from ( select child.path,child.parent, parent.nama parent_nama, parent.link parent_link, child.id, child.nama, child.link, child.level, child.urut from lst_menu child INNER JOIN lst_menu parent on child.parent = parent.id and parent.active=true where child.active=true ) x
where (parent_nama LIKE CONCAT('%', #{search}, '%') OR parent_link LIKE CONCAT('%', #{search}, '%') OR id LIKE CONCAT('%', #{search}, '%') OR nama LIKE CONCAT('%', #{search}, '%') OR urut LIKE CONCAT('%', #{search}, '%'))
order by path, urut LIMIT #{offset}, #{rowcount}

SELECT count(*) FROM ( select * from ( select child.path,child.parent, parent.nama parent_nama, parent.link parent_link, child.id, child.nama, child.link, child.level, child.urut from lst_menu child INNER JOIN lst_menu parent on child.parent = parent.id and parent.active=true where child.active=true ) x
where (parent_nama LIKE CONCAT('%', #{value}, '%') OR parent_link LIKE CONCAT('%', #{value}, '%') OR id LIKE CONCAT('%', #{value}, '%') OR nama LIKE CONCAT('%', #{value}, '%') OR urut LIKE CONCAT('%', #{value}, '%'))
)count

SELECT a.*, ifnull(b.colly_naik, 0) colly_naik
FROM mst_trans_det a
	 LEFT JOIN (SELECT trans_id, trans_urut, SUM(colly_naik) colly_naik FROM mst_delivery_det GROUP BY trans_id, trans_urut ) b ON a.trans_id = b.trans_id AND a.urut = b.trans_urut
WHERE a.trans_id = #{trans_id}
ORDER BY a.urut ;

SELECT d.*, s.nama supir_nama, k.nama kapal_nama FROM mst_delivery d, lst_supir s, lst_kapal k WHERE d.supir_id = s.id AND d.kode_kapal = k.kode
AND ( UPPER(d.no_polisi) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(s.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(k.kode) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(k.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
AND d.tgl_kirim >= #{periodFrom}
AND d.tgl_kirim <= #{periodTo}
ORDER BY d.id DESC LIMIT #{offset}, #{rowcount}

SELECT count(*) FROM mst_delivery d, lst_supir s, lst_kapal k WHERE d.supir_id = s.id AND d.kode_kapal = k.kode
AND ( UPPER(d.no_polisi) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(s.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(k.kode) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(k.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
AND d.tgl_kirim >= #{periodFrom}
AND d.tgl_kirim <= #{periodTo}

SELECT * FROM lst_biaya b
WHERE (b.id LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(b.nama) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
ORDER BY b.nama LIMIT #{offset}, #{rowcount}

SELECT IFNULL(SUM(dd.colly_naik), 0) colly_naik 
FROM mst_delivery_det dd, mst_delivery d
WHERE d.id = dd.delivery_id AND d.cancel = 0 AND dd.delivery_id <> #{delivery_id} AND dd.trans_id = #{trans_id} AND dd.trans_urut = #{trans_urut}

SELECT p.payment_id, p.no_payment, p.paid_date, upper(p.dk) dk, p.nominal, p.due_date, p.keterangan, p.createdate, c.keterangan pay_ket, cr.username createuser, p.cancel FROM mst_payment p LEFT OUTER JOIN lst_user cr ON p.createby = cr.id LEFT OUTER JOIN (SELECT jenis, keterangan FROM lst_config WHERE id = 2) c ON p.pay_mode = c.jenis WHERE p.trans_id is null
AND ( UPPER(p.no_payment) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(p.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(c.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
AND p.paid_date >= #{periodFrom}
AND p.paid_date <= #{periodTo}
ORDER BY createdate DESC LIMIT #{offset}, #{rowcount}

SELECT count(*) FROM mst_payment p LEFT OUTER JOIN lst_user cr ON p.createby = cr.id LEFT OUTER JOIN (SELECT jenis, keterangan FROM lst_config WHERE id = 2) c ON p.pay_mode = c.jenis WHERE p.trans_id is null
AND ( UPPER(p.no_payment) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(p.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(c.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
AND p.paid_date >= #{periodFrom}
AND p.paid_date <= #{periodTo}

SELECT p.payment_id, p.no_payment, p.paid_date, upper(p.dk) dk, p.nominal, p.due_date, p.keterangan, p.createdate, c.keterangan pay_ket, cr.username createuser, t.no_stt, p.cancel FROM mst_payment p INNER JOIN mst_trans t ON p.trans_id = t.id LEFT OUTER JOIN lst_user cr ON p.createby = cr.id LEFT OUTER JOIN (SELECT jenis, keterangan FROM lst_config WHERE id = 2) c ON p.pay_mode = c.jenis
WHERE ( UPPER(p.no_payment) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(p.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(c.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(t.no_stt) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
AND p.paid_date >= #{periodFrom}
AND p.paid_date <= #{periodTo}
ORDER BY createdate DESC LIMIT #{offset}, #{rowcount}

SELECT count(*) FROM mst_payment p INNER JOIN mst_trans t ON p.trans_id = t.id LEFT OUTER JOIN lst_user cr ON p.createby = cr.id LEFT OUTER JOIN (SELECT jenis, keterangan FROM lst_config WHERE id = 2) c ON p.pay_mode = c.jenis
WHERE ( UPPER(p.no_payment) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(p.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(c.keterangan) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') OR UPPER(t.no_stt) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
AND p.paid_date >= #{periodFrom}
AND p.paid_date <= #{periodTo}

SELECT DISTINCT CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.id ELSE c2.id END id, CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.nama ELSE c2.nama END nama, CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.limit_hutang ELSE c2.limit_hutang END limit_hutang, SUM(tr.total_harga) total_harga, SUM(tr.potongan) potongan, IFNULL(payment.paid, 0) paid, SUM(tr.remain) remain, MIN(tgl_stt) since FROM mst_trans tr LEFT JOIN mst_customer c ON c.id = tr.customer_id LEFT JOIN mst_customer c2 ON c2.id = tr.tujuan_id LEFT JOIN (SELECT trans_id, SUM(nominal) paid FROM mst_payment WHERE IFNULL(cancel, 0) = 0 AND trans_id IS NOT NULL ) payment ON payment.trans_id = tr.id WHERE IFNULL(tr.cancel, 0) = 0 AND tr.remain > 0
AND ( UPPER(CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.nama ELSE c2.nama END) LIKE CONCAT('%', UPPER(TRIM(#{search})), '%') )
GROUP BY CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.id ELSE c2.id END, CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.nama ELSE c2.nama END, CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.limit_hutang ELSE c2.limit_hutang END ORDER BY since, CASE WHEN (tr.cabang_id = tr.pay_mode) or tr.pay_mode = 0 THEN c.nama ELSE c2.nama END;
